import React, { useState } from 'react';
import styled from 'styled-components';
import { toast } from 'react-toastify';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faUpload, 
  faImage, 
  faHandPaper, 
  faFileText,
  faPlay,
  faTrash
} from '@fortawesome/free-solid-svg-icons';

import { multimodalAPI } from '../services/api';

const MainContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 15px;
  padding: 30px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
  
  h2 {
    font-size: 1.3rem;
    color: #2c3e50;
    margin-bottom: 15px;
    display: flex;
    align-items: center;
    
    .icon {
      margin-right: 8px;
      color: #667eea;
    }
  }
`;

const DataInputGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 20px;
  margin-bottom: 30px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const InputCard = styled.div`
  border: 2px solid #e9ecef;
  border-radius: 12px;
  padding: 20px;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #667eea;
    box-shadow: 0 5px 15px rgba(102, 126, 234, 0.1);
  }
  
  h3 {
    margin: 0 0 15px 0;
    color: #2c3e50;
    font-size: 1rem;
    display: flex;
    align-items: center;
    
    .icon {
      margin-right: 8px;
      color: #667eea;
    }
  }
`;

const FileUploadArea = styled.div`
  border: 2px dashed #dee2e6;
  border-radius: 8px;
  padding: 30px;
  text-align: center;
  transition: all 0.3s ease;
  cursor: pointer;
  min-height: 120px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  
  &:hover, &.dragover {
    border-color: #667eea;
    background: rgba(102, 126, 234, 0.05);
  }
  
  .upload-icon {
    font-size: 2rem;
    color: #dee2e6;
    margin-bottom: 10px;
  }
  
  &.has-file {
    border-color: #28a745;
    background: rgba(40, 167, 69, 0.05);
    
    .upload-icon {
      color: #28a745;
    }
  }
  
  p {
    margin: 0;
    color: #6c757d;
    font-size: 0.9rem;
  }
  
  .file-name {
    color: #28a745;
    font-weight: 500;
    margin-top: 5px;
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  min-height: 100px;
  padding: 12px;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  font-family: inherit;
  font-size: 0.9rem;
  resize: vertical;
  transition: border-color 0.3s ease;
  
  &:focus {
    outline: none;
    border-color: #667eea;
  }
  
  &::placeholder {
    color: #adb5bd;
  }
`;

const PromptSection = styled.div`
  border: 2px solid #f8f9fa;
  border-radius: 12px;
  padding: 20px;
  margin-bottom: 20px;
`;

const PromptTypeSelector = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 15px;
  flex-wrap: wrap;
`;

const RadioOption = styled.label`
  display: flex;
  align-items: center;
  cursor: pointer;
  
  input {
    margin-right: 8px;
  }
  
  span {
    font-size: 0.9rem;
    color: #495057;
  }
`;

const PromptEditor = styled.div`
  h4 {
    margin: 0 0 10px 0;
    color: #2c3e50;
    font-size: 1rem;
    display: flex;
    align-items: center;
    
    .icon {
      margin-right: 8px;
      color: #667eea;
    }
  }
`;

const PromptTextArea = styled.textarea`
  width: 100%;
  min-height: 120px;
  padding: 15px;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  font-family: 'Courier New', monospace;
  font-size: 0.85rem;
  line-height: 1.5;
  background: #f8f9fa;
  resize: vertical;
  
  &:focus {
    outline: none;
    border-color: #667eea;
    background: white;
  }
`;

const CheckboxOption = styled.label`
  display: flex;
  align-items: center;
  margin-top: 10px;
  cursor: pointer;
  
  input {
    margin-right: 8px;
  }
  
  span {
    font-size: 0.9rem;
    color: #495057;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 15px;
  justify-content: center;
  margin-bottom: 30px;
`;

const Button = styled.button`
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
  
  &.primary {
    background: #667eea;
    color: white;
    
    &:hover {
      background: #5a67d8;
      transform: translateY(-2px);
    }
  }
  
  &.secondary {
    background: #e9ecef;
    color: #495057;
    
    &:hover {
      background: #dee2e6;
    }
  }
  
  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const ResponseSection = styled.div`
  border: 2px solid #f8f9fa;
  border-radius: 12px;
  padding: 20px;
  min-height: 200px;
  
  h3 {
    margin: 0 0 15px 0;
    color: #2c3e50;
    font-size: 1.1rem;
    display: flex;
    align-items: center;
    
    .icon {
      margin-right: 8px;
      color: #28a745;
    }
  }
  
  .no-response {
    text-align: center;
    color: #adb5bd;
    font-style: italic;
    padding: 40px 20px;
  }
  
  .response-content {
    background: #f8f9fa;
    border-radius: 8px;
    padding: 20px;
    white-space: pre-wrap;
    font-family: inherit;
    line-height: 1.6;
  }
`;

const MainInterface = ({ setIsLoading }) => {
  const [tactileFile, setTactileFile] = useState(null);
  const [visualFile, setVisualFile] = useState(null);
  const [textualInput, setTextualInput] = useState('');
  const [promptType, setPromptType] = useState('Tactile-Text');
  const [basePrompt, setBasePrompt] = useState('请分析以下内容并提供详细的描述和见解：');
  const [promptText, setPromptText] = useState('');
  const [isPromptManuallyEdited, setIsPromptManuallyEdited] = useState(false);
  const [addContextualInfo, setAddContextualInfo] = useState(false);
  const [response, setResponse] = useState(null);

  // 根据输入动态生成prompt
  const generatePrompt = () => {
    let prompt = basePrompt + '\n\n';
    
    // 根据prompt类型添加相应的上下文
    if (promptType === 'Tactile-Text') {
      if (tactileFile) {
        prompt += `触觉数据文件: ${tactileFile.name}\n`;
      }
      if (textualInput.trim()) {
        prompt += `文本内容: ${textualInput}\n`;
      }
    } else if (promptType === 'Vision-Text') {
      if (visualFile) {
        prompt += `图片文件: ${visualFile.name}\n`;
      }
      if (textualInput.trim()) {
        prompt += `文本内容: ${textualInput}\n`;
      }
    } else if (promptType === 'Tactile-Vision-Text Combined') {
      if (tactileFile) {
        prompt += `触觉数据文件: ${tactileFile.name}\n`;
      }
      if (visualFile) {
        prompt += `图片文件: ${visualFile.name}\n`;
      }
      if (textualInput.trim()) {
        prompt += `文本内容: ${textualInput}\n`;
      }
    } else if (promptType === 'Question Answering') {
      prompt = '请基于以下信息回答问题：\n\n';
      if (tactileFile) {
        prompt += `触觉数据: ${tactileFile.name}\n`;
      }
      if (visualFile) {
        prompt += `视觉信息: ${visualFile.name}\n`;
      }
      if (textualInput.trim()) {
        prompt += `问题: ${textualInput}\n`;
      }
    }
    
    if (addContextualInfo) {
      prompt += '\n请提供详细分析，并考虑所有可用模态之间的相互作用。';
    }
    
    return prompt.trim();
  };

  // 监听输入变化，自动更新prompt
  React.useEffect(() => {
    const newPrompt = generatePrompt();
    setPromptText(newPrompt);
  }, [tactileFile, visualFile, textualInput, promptType, basePrompt, addContextualInfo]);

  const handleFileUpload = (file, type) => {
    if (type === 'tactile') {
      setTactileFile(file);
    } else if (type === 'visual') {
      setVisualFile(file);
    }
  };

  const handleDrop = (e, type) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files[0], type);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const clearInputs = () => {
    setTactileFile(null);
    setVisualFile(null);
    setTextualInput('');
    setResponse(null);
  };

  const generateResponse = async () => {
    try {
      setIsLoading(true);
      
      // 检查至少有一种输入
      if (!tactileFile && !visualFile && !textualInput.trim()) {
        toast.error('请至少提供一种输入：触觉文件、图片或文本');
        return;
      }
      
      // 准备数据
      const formData = new FormData();
      
      if (tactileFile) {
        formData.append('tactile_file', tactileFile);
      }
      
      if (visualFile) {
        formData.append('image', visualFile);
      }
      
      if (textualInput.trim()) {
        formData.append('text_context', textualInput);
      }
      
      formData.append('prompt', promptText);
      formData.append('prompt_type', promptType);
      formData.append('add_contextual_info', addContextualInfo);
      
      console.log('发送请求到API...', {
        prompt: promptText,
        promptType: promptType,
        hasImage: !!visualFile,
        hasTactileFile: !!tactileFile,
        hasTextContext: !!textualInput.trim()
      });
      
      // 调用统一的多模态分析API
      const result = await multimodalAPI.processUnifiedAnalysis(formData);
      
      console.log('API响应:', result);
      
      if (result.success) {
        setResponse(result.response);
        toast.success('分析完成！');
      } else {
        toast.error(`分析失败: ${result.error}`);
      }
      
    } catch (error) {
      console.error('请求错误:', error);
      toast.error(`请求失败: ${error.message || error.error || '未知错误'}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <MainContainer>
      {/* Data Input Section */}
      <Section>
        <h2>
          <FontAwesomeIcon icon={faUpload} className="icon" />
          Data Input Section
        </h2>
        
        <DataInputGrid>
          {/* Tactile Data Input */}
          <InputCard>
            <h3>
              <FontAwesomeIcon icon={faHandPaper} className="icon" />
              Tactile Data Input
            </h3>
            <FileUploadArea
              className={tactileFile ? 'has-file' : ''}
              onDrop={(e) => handleDrop(e, 'tactile')}
              onDragOver={handleDragOver}
              onClick={() => document.getElementById('tactile-input').click()}
            >
              <FontAwesomeIcon icon={faUpload} className="upload-icon" />
              {tactileFile ? (
                <>
                  <p>Tactile file uploaded</p>
                  <p className="file-name">{tactileFile.name}</p>
                </>
              ) : (
                <p>Upload Tactile File (Future)</p>
              )}
              <input
                id="tactile-input"
                type="file"
                style={{ display: 'none' }}
                accept=".csv,.json,.txt"
                onChange={(e) => handleFileUpload(e.target.files[0], 'tactile')}
              />
            </FileUploadArea>
          </InputCard>

          {/* Visual Data Input */}
          <InputCard>
            <h3>
              <FontAwesomeIcon icon={faImage} className="icon" />
              Visual Data Input
            </h3>
            <FileUploadArea
              className={visualFile ? 'has-file' : ''}
              onDrop={(e) => handleDrop(e, 'visual')}
              onDragOver={handleDragOver}
              onClick={() => document.getElementById('visual-input').click()}
            >
              <FontAwesomeIcon icon={faUpload} className="upload-icon" />
              {visualFile ? (
                <>
                  <p>Image uploaded</p>
                  <p className="file-name">{visualFile.name}</p>
                </>
              ) : (
                <p>No image uploaded</p>
              )}
              <input
                id="visual-input"
                type="file"
                style={{ display: 'none' }}
                accept="image/*"
                onChange={(e) => handleFileUpload(e.target.files[0], 'visual')}
              />
            </FileUploadArea>
            <TextArea
              placeholder="Manual image description (optional)"
              style={{ marginTop: '10px', minHeight: '60px' }}
            />
          </InputCard>

          {/* Textual Input */}
          <InputCard>
            <h3>
              <FontAwesomeIcon icon={faFileText} className="icon" />
              Textual Input
            </h3>
            <TextArea
              placeholder="Enter additional text, questions, or queries here..."
              value={textualInput}
              onChange={(e) => setTextualInput(e.target.value)}
            />
          </InputCard>
        </DataInputGrid>
      </Section>

      {/* Prompt Engineering & Controls */}
      <Section>
        <h2>
          <FontAwesomeIcon icon={faPlay} className="icon" />
          Prompt Engineering & Controls
        </h2>
        
        <PromptSection>
          <div style={{ marginBottom: '20px' }}>
            <h4 style={{ margin: '0 0 10px 0', color: '#2c3e50' }}>Prompt Type Selection</h4>
            <PromptTypeSelector>
              <RadioOption>
                <input
                  type="radio"
                  name="promptType"
                  value="Tactile-Text"
                  checked={promptType === 'Tactile-Text'}
                  onChange={(e) => setPromptType(e.target.value)}
                />
                <span>Tactile-Text</span>
              </RadioOption>
              <RadioOption>
                <input
                  type="radio"
                  name="promptType"
                  value="Vision-Text"
                  checked={promptType === 'Vision-Text'}
                  onChange={(e) => setPromptType(e.target.value)}
                />
                <span>Vision-Text</span>
              </RadioOption>
              <RadioOption>
                <input
                  type="radio"
                  name="promptType"
                  value="Tactile-Vision-Text Combined"
                  checked={promptType === 'Tactile-Vision-Text Combined'}
                  onChange={(e) => setPromptType(e.target.value)}
                />
                <span>Tactile-Vision-Text Combined</span>
              </RadioOption>
              <RadioOption>
                <input
                  type="radio"
                  name="promptType"
                  value="Question Answering"
                  checked={promptType === 'Question Answering'}
                  onChange={(e) => setPromptType(e.target.value)}
                />
                <span>Question Answering</span>
              </RadioOption>
            </PromptTypeSelector>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <h4 style={{ margin: '0 0 10px 0', color: '#2c3e50' }}>Base Prompt Template</h4>
            <TextArea
              value={basePrompt}
              onChange={(e) => setBasePrompt(e.target.value)}
              placeholder="输入基础提示词模板..."
              style={{ minHeight: '60px' }}
            />
          </div>

          <PromptEditor>
            <h4>
              <FontAwesomeIcon icon={faFileText} className="icon" />
              Prompt Preview (Auto-Generated)
            </h4>
            <PromptTextArea
              value={promptText}
              onChange={(e) => setPromptText(e.target.value)}
              placeholder="这里会根据上面的输入自动生成完整的提示词..."
            />
            <CheckboxOption>
              <input
                type="checkbox"
                checked={addContextualInfo}
                onChange={(e) => setAddContextualInfo(e.target.checked)}
              />
              <span>Add Contextual Information</span>
            </CheckboxOption>
          </PromptEditor>
        </PromptSection>

        <ButtonGroup>
          <Button className="primary" onClick={generateResponse}>
            <FontAwesomeIcon icon={faPlay} />
            Generate Response
          </Button>
          <Button className="secondary" onClick={clearInputs}>
            <FontAwesomeIcon icon={faTrash} />
            Clear Inputs
          </Button>
        </ButtonGroup>
      </Section>

      {/* AI Response Output */}
      <Section>
        <ResponseSection>
          <h3>
            <FontAwesomeIcon icon={faPlay} className="icon" />
            AI Response Output
          </h3>
          {response ? (
            <div className="response-content">
              {response}
            </div>
          ) : (
            <div className="no-response">
              <p>No response generated yet</p>
              <p>Configure your inputs and click "Generate Response" to see AI analysis</p>
            </div>
          )}
        </ResponseSection>
      </Section>
    </MainContainer>
  );
};

export default MainInterface;
